mapper.insert:
insert into user(
id
, name
, birthdate
, deleted
, created_at
, created_by
, updated_at
, updated_by
, deleted_at
, deleted_by
)
values (
#{id}
, #{name}
, #{birthdate}
, #{deleted}
, #{createdAt}
, #{createdBy}
, #{updatedAt}
, #{updatedBy}
, #{deletedAt}
, #{deletedBy}
)

mapper.update:
update user set 
deleted = '1' 
, deleted_at = #{deletedAt}
where id = #{id}

update user set 
name = #{name}
, birthdate = #{birthdate} 
, updated_at = #{updatedAt}
where id = #{id}
